using System;
using System.IO;
using System.IO.Compression;
using System.Linq;

var nupkgFiles = new[] {
    ("2.88.0-preview.179 (reporter)", "views-forms-preview179.nupkg"),
    ("2.88.0-preview.127 (first netcoreapp3.1)", "views-forms-preview127.nupkg"),
    ("2.88.9 (latest stable 2.88.x)", "views-forms-stable.nupkg"),
};

foreach (var (label, file) in nupkgFiles)
{
    var path = Path.Combine("..", file);
    if (!File.Exists(path)) { Console.WriteLine($"SKIP: {label} - file not found"); continue; }
    Console.WriteLine($"\n=== {label} ===");
    using var zip = ZipFile.OpenRead(path);
    var netcoreEntries = zip.Entries.Where(e => e.FullName.Contains("netcoreapp3.1")).OrderBy(e => e.FullName).ToList();
    Console.WriteLine($"  netcoreapp3.1 entries: {netcoreEntries.Count}");
    foreach (var entry in netcoreEntries)
        Console.WriteLine($"    {entry.FullName} ({entry.Length} bytes)");
    var dllCount = netcoreEntries.Count(e => e.FullName.EndsWith(".dll"));
    var placeholderCount = netcoreEntries.Count(e => e.FullName.EndsWith("_._"));
    Console.WriteLine($"  DLLs: {dllCount}, Placeholders (_._): {placeholderCount}");
}
