using System;
using System.IO;
using System.Runtime.InteropServices;
using SkiaSharp;

Console.WriteLine($"SkiaSharp version: {typeof(SKBitmap).Assembly.GetName().Version}");
Console.WriteLine($"OS: {RuntimeInformation.OSDescription}");
Console.WriteLine($"Arch: {RuntimeInformation.ProcessArchitecture}");

var pngFiles = new[] { "baboon.png", "color-wheel.png", "index8.png", "testimage.png" };
int failures = 0;

// Test 1: SKBitmap.Decode(string path)
foreach (var file in pngFiles)
{
    var path = Path.Combine(AppContext.BaseDirectory, file);
    if (!File.Exists(path)) path = Path.Combine(Directory.GetCurrentDirectory(), file);
    if (!File.Exists(path)) { Console.WriteLine($"[SKIP] {file}"); continue; }
    var bmp = SKBitmap.Decode(path);
    if (bmp == null) { Console.WriteLine($"[FAIL] {file}: null"); failures++; }
    else { Console.WriteLine($"[PASS] {file}: {bmp.Width}x{bmp.Height}"); bmp.Dispose(); }
}

// Test 2: SKCodec.Create(stream)
foreach (var file in pngFiles)
{
    var path = Path.Combine(AppContext.BaseDirectory, file);
    if (!File.Exists(path)) path = Path.Combine(Directory.GetCurrentDirectory(), file);
    if (!File.Exists(path)) continue;
    using var stream = File.OpenRead(path);
    using var skStream = new SKManagedStream(stream);
    using var codec = SKCodec.Create(skStream, out var result);
    if (codec == null) { Console.WriteLine($"[FAIL] {file}: {result}"); failures++; }
    else Console.WriteLine($"[PASS] {file}: {codec.EncodedFormat}, {result}");
}

// Test 3: Large gradient PNG + truncated edge cases
using (var lg = new SKBitmap(2048, 2048))
{
    for (int y = 0; y < 2048; y++)
        for (int x = 0; x < 2048; x++)
            lg.SetPixel(x, y, new SKColor((byte)(x & 0xFF), (byte)(y & 0xFF), 128));
    using var img = SKImage.FromBitmap(lg);
    using var enc = img.Encode(SKEncodedImageFormat.Png, 100);
    var tmp = Path.Combine(Path.GetTempPath(), "large.png");
    using (var fs = File.Create(tmp)) enc.SaveTo(fs);
    using var dec = SKBitmap.Decode(tmp);
    Console.WriteLine(dec == null ? "[FAIL] Large PNG" : $"[PASS] Large: {dec.Width}x{dec.Height}");
    File.Delete(tmp);
}

Console.WriteLine($"Total failures: {failures}");
return failures > 0 ? 1 : 0;
