using SkiaSharp;

Console.WriteLine($"SkiaSharp version: {typeof(SKMatrix).Assembly.GetName().Version}");
Console.WriteLine($"Runtime: {System.Runtime.InteropServices.RuntimeInformation.FrameworkDescription}");
Console.WriteLine($"OS: {System.Runtime.InteropServices.RuntimeInformation.OSDescription}");
Console.WriteLine();

var inputRect = new SKRect();
inputRect.Left = 25;
inputRect.Right = 15;
inputRect.Bottom = 2;
inputRect.Top = 10;

Console.WriteLine($"Input rect:  Left={inputRect.Left}, Top={inputRect.Top}, Right={inputRect.Right}, Bottom={inputRect.Bottom}");

var resultRect = SKMatrix.CreateIdentity().MapRect(inputRect);

Console.WriteLine($"Result rect: Left={resultRect.Left}, Top={resultRect.Top}, Right={resultRect.Right}, Bottom={resultRect.Bottom}");
Console.WriteLine();

bool leftMatch = resultRect.Left == inputRect.Left;
bool rightMatch = resultRect.Right == inputRect.Right;
bool topMatch = resultRect.Top == inputRect.Top;
bool bottomMatch = resultRect.Bottom == inputRect.Bottom;

Console.WriteLine($"Left   matches: {leftMatch} (expected={inputRect.Left}, got={resultRect.Left})");
Console.WriteLine($"Right  matches: {rightMatch} (expected={inputRect.Right}, got={resultRect.Right})");
Console.WriteLine($"Top    matches: {topMatch} (expected={inputRect.Top}, got={resultRect.Top})");
Console.WriteLine($"Bottom matches: {bottomMatch} (expected={inputRect.Bottom}, got={resultRect.Bottom})");
Console.WriteLine();

if (leftMatch && rightMatch && topMatch && bottomMatch)
    Console.WriteLine("SUCCESS: MapRect preserved rect coordinates (bug NOT present)");
else
{
    Console.WriteLine("BUG CONFIRMED: MapRect normalized/standardized the rect coordinates");
    Console.WriteLine($"  Left/Right swapped: {resultRect.Left == inputRect.Right && resultRect.Right == inputRect.Left}");
    Console.WriteLine($"  Top/Bottom swapped: {resultRect.Top == inputRect.Bottom && resultRect.Bottom == inputRect.Top}");
}
