// Reproduction of issue #3279 - SKMatrix APIs from v2.88.9 used in v3.x
using SkiaSharp;

// Error 5: CS0117 - 'SKMatrix' does not contain a definition for 'MakeIdentity'
SKMatrix Matrix = SKMatrix.MakeIdentity();

// Error 2: CS0117 - 'SKMatrix' does not contain a definition for 'MakeTranslation'
var translation = SKMatrix.MakeTranslation(100f, 200f);

// Error 3: CS0117 - 'SKMatrix' does not contain a definition for 'MakeScale'
var scale = SKMatrix.MakeScale(2.0f, 2.0f, 50f, 50f);

// Error 4: CS0117 - 'SKMatrix' does not contain a definition for 'MakeRotation'
var rotation = SKMatrix.MakeRotation(1.57f, 50f, 50f);

// Error 1: CS1501 - No overload for method 'PostConcat' takes 2 arguments
SKMatrix.PostConcat(ref Matrix, translation);

Console.WriteLine($"Matrix: {Matrix}");
