using SkiaSharp;

var center = new SKPoint(100, 100);
float scale = 2.0f;
float angle = 45.0f;
float width = 800, height = 600;

// Old 2.x APIs:
var matrix = SKMatrix.MakeIdentity();
var translation = SKMatrix.MakeTranslation(width / 2.0f, height / 2.0f);
var scaleMatrix = SKMatrix.MakeScale(scale, scale, center.X, center.Y);
var rotation = SKMatrix.MakeRotation(angle, center.X, center.Y);
SKMatrix.PostConcat(ref matrix, translation);

Console.WriteLine($"Matrix: ScaleX={matrix.ScaleX}, TransX={matrix.TransX}");

using SkiaSharp;

var center = new SKPoint(100, 100);
float scale = 2.0f;
float angle = 45.0f;
float width = 800, height = 600;

// New 3.x APIs:
var matrix = SKMatrix.Identity;
var translation = SKMatrix.CreateTranslation(width / 2.0f, height / 2.0f);
var scaleMatrix = SKMatrix.CreateScale(scale, scale, center.X, center.Y);
var rotation = SKMatrix.CreateRotation(angle, center.X, center.Y);
matrix = matrix.PostConcat(translation);

Console.WriteLine($"Matrix: ScaleX={matrix.ScaleX}, TransX={matrix.TransX}");
