using SkiaSharp;

// Reproduction for #3279: Test SKMatrix Make* APIs that existed in 2.88.9
// All 5 API calls from the reporter's issue

// 1. MakeIdentity
SKMatrix Matrix = SKMatrix.MakeIdentity();
Console.WriteLine($"1. MakeIdentity: ScaleX={Matrix.ScaleX}, ScaleY={Matrix.ScaleY}");

// 2. MakeTranslation
var translation = SKMatrix.MakeTranslation(100f, 200f);
Console.WriteLine($"2. MakeTranslation: TransX={translation.TransX}, TransY={translation.TransY}");

// 3. MakeScale with pivot
var scale = SKMatrix.MakeScale(2f, 2f, 50f, 50f);
Console.WriteLine($"3. MakeScale: ScaleX={scale.ScaleX}, ScaleY={scale.ScaleY}");

// 4. MakeRotation with pivot
var rotation = SKMatrix.MakeRotation(0.5f, 50f, 50f);
Console.WriteLine($"4. MakeRotation: ScaleX={rotation.ScaleX}, SkewX={rotation.SkewX}");

// 5. PostConcat with ref (static method, 2 args)
SKMatrix.PostConcat(ref Matrix, translation);
Console.WriteLine($"5. PostConcat: TransX={Matrix.TransX}, TransY={Matrix.TransY}");

Console.WriteLine("SUCCESS: All SKMatrix operations completed");

using SkiaSharp;

public static class PostConcatReproTest {
    public static void Run() {
        SKMatrix Matrix = SKMatrix.CreateIdentity();
        var translation = SKMatrix.CreateTranslation(100f, 200f);
        // Old v2 API: static PostConcat(ref SKMatrix, SKMatrix)
        SKMatrix.PostConcat(ref Matrix, translation);
        System.Console.WriteLine($"PostConcat: TransX={Matrix.TransX}");
    }
}
