using SkiaSharp;

// Reproduction of issue #3279: SKMatrix APIs from v2.88.9 that fail in v3.x
// All 5 compiler errors the reporter mentioned

var Center = new SKPoint(100, 100);
float GfxScale = 2.0f;
float zAngle = 0.5f;
float displayWidth = 800f;
float displayHeight = 600f;

// Error 5: CS0117 - MakeIdentity
SKMatrix Matrix = SKMatrix.MakeIdentity();

// Error 2: CS0117 - MakeTranslation (via PostConcat)
SKMatrix.PostConcat(ref Matrix, SKMatrix.MakeTranslation(displayWidth / 2.0f, displayHeight / 2.0f));

// Error 3: CS0117 - MakeScale (via PostConcat)
SKMatrix.PostConcat(ref Matrix, SKMatrix.MakeScale(GfxScale, GfxScale, Center.X, Center.Y));

// Error 4: CS0117 - MakeRotation (via PostConcat)
SKMatrix.PostConcat(ref Matrix, SKMatrix.MakeRotation(zAngle, Center.X, Center.Y));

// Error 1: CS1501 - PostConcat with 2 args (ref + matrix)
var extraMatrix = new SKMatrix();
SKMatrix.PostConcat(ref Matrix, extraMatrix);

Console.WriteLine($"Final matrix: {Matrix}");
Console.WriteLine("All operations completed successfully.");
