using System;
using System.Runtime.InteropServices;
using SkiaSharp;

Console.WriteLine($"SkiaSharp Version: {typeof(SKBitmap).Assembly.GetName().Version}");
Console.WriteLine($"OS: {RuntimeInformation.OSDescription}");
Console.WriteLine($"Arch: {RuntimeInformation.ProcessArchitecture}");
Console.WriteLine($"Framework: {RuntimeInformation.FrameworkDescription}");

try
{
    using var bitmap = new SKBitmap(100, 100);
    using var canvas = new SKCanvas(bitmap);
    canvas.Clear(SKColors.Blue);

    var pixel = bitmap.GetPixel(50, 50);
    Console.WriteLine($"Pixel at (50,50): {pixel}");
    Console.WriteLine($"Expected: Color [A=255, R=0, G=0, B=255]");
    Console.WriteLine($"Match: {pixel == SKColors.Blue}");

    Console.WriteLine("SUCCESS: No crash occurred.");
}
catch (Exception ex)
{
    Console.WriteLine($"CRASH: {ex.GetType().Name}: {ex.Message}");
    Console.WriteLine(ex.StackTrace);
}
