using System;
using System.IO;
using System.Linq;
using System.Runtime.InteropServices;
using SkiaSharp;

Console.WriteLine($"SkiaSharp version: {typeof(SKBitmap).Assembly.GetName().Version}");

// Test 1: Check if Gray16 exists in SKColorType enum
var colorTypes = Enum.GetNames<SKColorType>();
var hasGray16 = colorTypes.Contains("Gray16");
Console.WriteLine($"Has Gray16: {hasGray16}");

// Test 2: Decode 16-bit grayscale PNG
var pngBytes = File.ReadAllBytes("gray16_test.png");
using var skData = SKData.CreateCopy(pngBytes);
using var codec = SKCodec.Create(skData);
Console.WriteLine($"Codec: ColorType={codec?.Info.ColorType}, BitsPerPixel={codec?.Info.BitsPerPixel}");

using var bitmap = SKBitmap.Decode(skData);
Console.WriteLine($"Decoded: ColorType={bitmap?.ColorType}, BytesPerPixel={bitmap?.BytesPerPixel}");

var p00 = bitmap?.GetPixel(0, 0); // was 0x0000
var p10 = bitmap?.GetPixel(1, 0); // was 0x4000
var p20 = bitmap?.GetPixel(2, 0); // was 0x8000
var p30 = bitmap?.GetPixel(3, 0); // was 0xFFFF
Console.WriteLine($"Pixel(0,0)=0x0000: {p00}");
Console.WriteLine($"Pixel(1,0)=0x4000: {p10}");
Console.WriteLine($"Pixel(2,0)=0x8000: {p20}");
Console.WriteLine($"Pixel(3,0)=0xFFFF: {p30}");
Console.WriteLine($"Precision lost: decoded as {bitmap?.ColorType}");
