using SkiaSharp;

Console.WriteLine("Testing SkiaSharp with NativeAssets.Linux.NoDependencies");
Console.WriteLine($"SkiaSharp Version: {typeof(SKBitmap).Assembly.GetName().Version}");

try
{
    using var bitmap = new SKBitmap(100, 100);
    using var canvas = new SKCanvas(bitmap);
    using var paint = new SKPaint { Color = SKColors.Red };
    canvas.DrawRect(new SKRect(0, 0, 100, 100), paint);
    Console.WriteLine("SUCCESS: Created and drew on bitmap");
    Console.WriteLine($"Bitmap size: {bitmap.Width}x{bitmap.Height}");
}
catch (Exception ex)
{
    Console.WriteLine($"FAILURE: {ex.GetType().Name}: {ex.Message}");
    if (ex.InnerException != null)
    {
        Console.WriteLine($"Inner: {ex.InnerException.GetType().Name}: {ex.InnerException.Message}");
    }
    return 1;
}
return 0;
