using System;
using System.Runtime.InteropServices;
using SkiaSharp;

Console.WriteLine("=== SkiaSharp 3.119.1 Native Load Test ===");
Console.WriteLine($"OS: {RuntimeInformation.OSDescription}");
Console.WriteLine($"Architecture: {RuntimeInformation.ProcessArchitecture}");
Console.WriteLine($".NET: {RuntimeInformation.FrameworkDescription}");
Console.WriteLine();

try
{
    // Test basic SkiaSharp loading (non-WinUI)
    using var bitmap = new SKBitmap(100, 100);
    using var canvas = new SKCanvas(bitmap);
    canvas.Clear(SKColors.White);
    canvas.DrawRect(10, 10, 80, 80, new SKPaint { Color = SKColors.Blue });

    using var image = SKImage.FromBitmap(bitmap);
    using var data = image.Encode(SKEncodedImageFormat.Png, 100);

    Console.WriteLine("✅ SkiaSharp core library loaded successfully");
    Console.WriteLine($"   Created 100x100 bitmap and rendered blue rectangle");
    Console.WriteLine($"   Encoded to PNG: {data.Size} bytes");
    Console.WriteLine();

    // Save to verify rendering
    using var stream = File.OpenWrite("/tmp/repro-3440-test.png");
    data.SaveTo(stream);
    Console.WriteLine($"✅ Saved test image to /tmp/repro-3440-test.png");
    Console.WriteLine();

    Console.WriteLine("Note: This test verifies SkiaSharp core functionality on macOS.");
    Console.WriteLine("The reported issue is specific to WinUI native components on Windows,");
    Console.WriteLine("which cannot be tested on this platform.");
}
catch (Exception ex)
{
    Console.WriteLine($"❌ Error: {ex.GetType().Name}: {ex.Message}");
    Console.WriteLine(ex.StackTrace);
    return 1;
}

return 0;
