using System.Diagnostics;
using Android.Opengl;
using Android.Util;
using SkiaSharp;
using SkiaSharp.Views.Android;

namespace Repro;

[Activity(Label = "FPS Test", MainLauncher = true)]
public class MainActivity : Android.App.Activity
{
    private SKGLTextureView? glView;
    private readonly Stopwatch sw = Stopwatch.StartNew();
    private TimeSpan lastFrame;
    private int frameCount;
    private double fpsSum;

    protected override void OnCreate(Bundle? savedInstanceState)
    {
        base.OnCreate(savedInstanceState);

        glView = new SKGLTextureView(this);
        glView.RenderMode = Rendermode.Continuously;
        glView.PaintSurface += OnPaintSurface;
        SetContentView(glView);
    }

    private void OnPaintSurface(object? sender, SKPaintGLSurfaceEventArgs e)
    {
        var now = sw.Elapsed;
        var delta = now - lastFrame;
        lastFrame = now;

        if (delta.TotalSeconds > 0)
        {
            var fps = 1.0 / delta.TotalSeconds;
            frameCount++;
            fpsSum += fps;

            if (frameCount % 60 == 0)
            {
                var avgFps = fpsSum / 60.0;
                Log.Debug("FPS_REPRO", $"Frame {frameCount}: Avg FPS = {avgFps:F1}, Last FPS = {fps:F1}");
                fpsSum = 0;
            }
        }

        var canvas = e.Surface.Canvas;
        canvas.Clear(SKColors.CornflowerBlue);
        using var paint = new SKPaint { Color = SKColors.White, TextSize = 48 };
        canvas.DrawText($"Frame: {frameCount}", 50, 100, paint);
    }

    protected override void OnResume()
    {
        base.OnResume();
        glView?.OnResume();
    }

    protected override void OnPause()
    {
        glView?.OnPause();
        base.OnPause();
    }
}
