using System;
using System.Diagnostics;
using SkiaSharp;

const int Width = 1920;
const int Height = 1080;
const int Iterations = 1000;
const int WarmupIterations = 50;

var info = new SKImageInfo(Width, Height);

Console.WriteLine($"Benchmarking SKSurface creation ({Iterations} iterations, {Width}x{Height})...");
Console.WriteLine();

// Warmup
for (int i = 0; i < WarmupIterations; i++)
{
    using var s = SKSurface.Create(info);
    var c = s.Canvas;
    c.Clear(SKColors.White);
    using var p = new SKPaint { Color = SKColors.Red };
    c.DrawCircle(100, 100, 50, p);
    c.Flush();
}

// Benchmark 1: Create new SKSurface each iteration
var sw = Stopwatch.StartNew();
for (int i = 0; i < Iterations; i++)
{
    using var surface = SKSurface.Create(info);
    var canvas = surface.Canvas;
    canvas.Clear(SKColors.White);
    using var paint = new SKPaint { Color = SKColors.Red };
    canvas.DrawCircle(100, 100, 50, paint);
    canvas.Flush();
}
sw.Stop();
var createEachTime = sw.Elapsed;
Console.WriteLine($"Create new SKSurface each time: {createEachTime.TotalMilliseconds:F2} ms total, {createEachTime.TotalMilliseconds / Iterations:F4} ms/iteration");

// Benchmark 2: Reuse single SKSurface
using (var ws = SKSurface.Create(info))
{
    for (int i = 0; i < WarmupIterations; i++)
    {
        var c = ws.Canvas;
        c.Clear(SKColors.White);
        using var p = new SKPaint { Color = SKColors.Red };
        c.DrawCircle(100, 100, 50, p);
        c.Flush();
    }
}

using var reusedSurface = SKSurface.Create(info);
sw.Restart();
for (int i = 0; i < Iterations; i++)
{
    var canvas = reusedSurface.Canvas;
    canvas.Clear(SKColors.White);
    using var paint = new SKPaint { Color = SKColors.Red };
    canvas.DrawCircle(100, 100, 50, paint);
    canvas.Flush();
}
sw.Stop();
var reuseTime = sw.Elapsed;
Console.WriteLine($"Reuse single SKSurface:         {reuseTime.TotalMilliseconds:F2} ms total, {reuseTime.TotalMilliseconds / Iterations:F4} ms/iteration");

// Benchmark 3: Just measure SKSurface.Create() alone
sw.Restart();
for (int i = 0; i < Iterations; i++)
{
    using var surface = SKSurface.Create(info);
}
sw.Stop();
var createOnly = sw.Elapsed;
Console.WriteLine($"SKSurface.Create() only:        {createOnly.TotalMilliseconds:F2} ms total, {createOnly.TotalMilliseconds / Iterations:F4} ms/iteration");

Console.WriteLine();
var overhead = createEachTime.TotalMilliseconds - reuseTime.TotalMilliseconds;
var pctOverhead = (overhead / reuseTime.TotalMilliseconds) * 100;
Console.WriteLine($"Surface creation overhead: {overhead:F2} ms total ({pctOverhead:F1}% of draw time)");
Console.WriteLine($"Per-frame cost of creating new surface: {createOnly.TotalMilliseconds / Iterations:F4} ms");
Console.WriteLine();

if (pctOverhead > 10)
    Console.WriteLine($"RESULT: SKSurface creation adds significant overhead ({pctOverhead:F1}%). Reuse is beneficial.");
else
    Console.WriteLine($"RESULT: SKSurface creation overhead is minimal ({pctOverhead:F1}%). Reuse may not be necessary.");
