using System;
using System.IO;
using SkiaSharp;

Console.WriteLine($"SkiaSharp Version: {typeof(SKBitmap).Assembly.GetName().Version}");
Console.WriteLine($"Runtime: {System.Runtime.InteropServices.RuntimeInformation.FrameworkDescription}");
Console.WriteLine($"OS: {System.Runtime.InteropServices.RuntimeInformation.OSDescription}");
Console.WriteLine($"Arch: {System.Runtime.InteropServices.RuntimeInformation.ProcessArchitecture}");
Console.WriteLine();

int sourceWidth = 14034;
int sourceHeight = 9921;
long pixelBytes = (long)sourceWidth * sourceHeight * 4;
Console.WriteLine($"=== Test 1: Allocate SKBitmap at {sourceWidth}x{sourceHeight} ({pixelBytes / 1024 / 1024}MB) ===");

SKBitmap? largeBitmap = null;
try
{
    largeBitmap = new SKBitmap(sourceWidth, sourceHeight);
    if (largeBitmap.GetPixels() == IntPtr.Zero)
        Console.WriteLine("ERROR: SKBitmap created but GetPixels() returned IntPtr.Zero");
    else
        Console.WriteLine($"SUCCESS: SKBitmap allocated ({largeBitmap.Width}x{largeBitmap.Height})");
}
catch (Exception ex)
{
    Console.WriteLine($"EXCEPTION: {ex.GetType().Name}: {ex.Message}");
}

// Generate large JPEG, resize using reporter's pattern, verify output
if (largeBitmap != null && largeBitmap.GetPixels() != IntPtr.Zero)
{
    using var canvas = new SKCanvas(largeBitmap);
    canvas.Clear(SKColors.Blue);
    using var paint = new SKPaint { Color = SKColors.Red };
    canvas.DrawRect(0, 0, sourceWidth / 2f, sourceHeight / 2f, paint);
    canvas.Flush();

    using var img = SKImage.FromBitmap(largeBitmap);
    using var encoded = img.Encode(SKEncodedImageFormat.Jpeg, 80);
    var imageData = encoded.ToArray();

    using var sourceStream = new SKMemoryStream(imageData);
    using var codec = SKCodec.Create(sourceStream);
    sourceStream.Seek(0);
    using var image = SKImage.FromEncodedData(SKData.Create(sourceStream));

    int newW = 1527, newH = 1080;
    var info = new SKImageInfo(newW, newH);
    using var surface = SKSurface.Create(info);
    using var rpaint = new SKPaint { IsAntialias = true, FilterQuality = SKFilterQuality.High };
    surface.Canvas.Clear(SKColors.White);
    surface.Canvas.DrawImage(image, new SKRect(0, 0, newW, newH), rpaint);
    surface.Canvas.Flush();

    using var newImage = surface.Snapshot();
    using var newImageData = newImage.Encode(SKEncodedImageFormat.Jpeg, 85);
    // Verify output is not blank by sampling pixels
    using var outputBitmap = SKBitmap.FromImage(SKImage.FromEncodedData(newImageData));
    int nonWhite = 0, total = 0;
    for (int y = 0; y < outputBitmap.Height; y += 50)
        for (int x = 0; x < outputBitmap.Width; x += 50)
        {
            total++;
            if (outputBitmap.GetPixel(x, y) != SKColors.White) nonWhite++;
        }
    Console.WriteLine($"Pixel check: {nonWhite}/{total} non-white");
    if (nonWhite == 0) Console.WriteLine("BUG: Output is blank");
    else Console.WriteLine("Output has content — not blank");
    largeBitmap.Dispose();
}
