        Assembly assembly = GetType().GetTypeInfo().Assembly;
        Stream stream = assembly.GetManifestResourceStream("wallpaper.jpg");
        SKManagedStream skStream = new SKManagedStream(stream);
        SKBitmap skBitmap = SKBitmap.Decode(skStream);

        int coordsX = 3;
        int coordsY = 3;

        int pX = skBitmap.Width / coordsX ;
        int pY = skBitmap.Height / coordsY;

        int count = 1;

        for (int i = 0; i < coordsX; i++)
        {
            for (int j = 0; j < coordsY; j++)
            {
                SKImageInfo info = new SKImageInfo(pX, pY);
                var pieceBitmap = new SKBitmap(info);

                using (var canvas = new SKCanvas(pieceBitmap))
                {
                   canvas.DrawBitmap(skBitmap, i * pX, j * pY);
                }

                SKImage skImg = SKImage.FromBitmap(pieceBitmap);

                SKData skImgData = skImg.Encode(SKEncodedImageFormat.Png, 100);

                IFolder rootFolder = FileSystem.Current.LocalStorage;
                IFolder folder = await rootFolder.CreateFolderAsync("PiecesFolder", CreationCollisionOption.OpenIfExists);
                IFile file = await folder.CreateFileAsync(string.Concat("Piece", count.ToString(),".png"), CreationCollisionOption.ReplaceExisting);

                using (Stream streamSave = await file.OpenAsync(FileAccess.ReadAndWrite))
                {
                    skImgData.SaveTo(streamSave);
                }

                Button btn = this.FindByName<Button>(string.Concat("button", count.ToString()));
                btn.Image = (FileImageSource)ImageSource.FromFile(file.Path);

                count++;
            }
