using System;
using System.Collections.Generic;
using System.IO;
using System.Runtime.InteropServices;
using System.Threading;
using System.Threading.Tasks;
using SkiaSharp;

Console.WriteLine($"SkiaSharp Version: {typeof(SKBitmap).Assembly.GetName().Version}");
Console.WriteLine($"OS: {RuntimeInformation.OSDescription}");
Console.WriteLine($"Arch: {RuntimeInformation.ProcessArchitecture}");

byte[] testImageBytes;
using (var bmp = new SKBitmap(800, 600))
{
    using var canvas = new SKCanvas(bmp);
    canvas.Clear(SKColors.Blue);
    using var paint = new SKPaint { Color = SKColors.Red, IsAntialias = true };
    for (int i = 0; i < 50; i++)
        canvas.DrawCircle(i * 16, i * 12, 40, paint);
    using var img = SKImage.FromBitmap(bmp);
    using var data = img.Encode(SKEncodedImageFormat.Png, 100);
    testImageBytes = data.ToArray();
}

byte[] jpegBytes;
using (var bmp = new SKBitmap(1024, 768))
{
    using var canvas = new SKCanvas(bmp);
    canvas.Clear(SKColors.Green);
    using var paint = new SKPaint { Color = SKColors.White, IsAntialias = true };
    for (int i = 0; i < 100; i++)
        canvas.DrawCircle(i * 10, i * 7, 30, paint);
    using var img = SKImage.FromBitmap(bmp);
    using var data = img.Encode(SKEncodedImageFormat.Jpeg, 90);
    jpegBytes = data.ToArray();
}

int[][] configs = { new[]{50, 200}, new[]{100, 100}, new[]{200, 50} };
int totalErrors = 0;

foreach (var cfg in configs)
{
    int numThreads = cfg[0];
    int itersPerThread = cfg[1];
    int decoded = 0;
    int roundErrors = 0;
    var tasks = new List<Task>();
    var barrier = new Barrier(numThreads);
    for (int i = 0; i < numThreads; i++)
    {
        int threadId = i;
        var task = Task.Run(() =>
        {
            barrier.SignalAndWait();
            byte[] imgBytes = threadId % 2 == 0 ? testImageBytes : jpegBytes;
            for (int j = 0; j < itersPerThread; j++)
            {
                try
                {
                    if (j % 3 == 0)
                    {
                        using var bitmap = SKBitmap.Decode(imgBytes);
                        if (bitmap == null) Interlocked.Increment(ref roundErrors);
                        else Interlocked.Increment(ref decoded);
                    }
                    else if (j % 3 == 1)
                    {
                        using var stream = new MemoryStream(imgBytes);
                        using var skStream = new SKManagedStream(stream);
                        using var bitmap = SKBitmap.Decode(skStream);
                        if (bitmap == null) Interlocked.Increment(ref roundErrors);
                        else Interlocked.Increment(ref decoded);
                    }
                    else
                    {
                        using var skData = SKData.CreateCopy(imgBytes);
                        using var codec = SKCodec.Create(skData);
                        if (codec == null) { Interlocked.Increment(ref roundErrors); continue; }
                        using var bitmap = SKBitmap.Decode(codec);
                        if (bitmap == null) Interlocked.Increment(ref roundErrors);
                        else Interlocked.Increment(ref decoded);
                    }
                }
                catch (Exception ex)
                {
                    Interlocked.Increment(ref roundErrors);
                    Console.WriteLine($"T{threadId}: {ex.GetType().Name}: {ex.Message}");
                }
            }
        });
        tasks.Add(task);
    }
    try { Task.WaitAll(tasks.ToArray()); }
    catch (AggregateException ae)
    {
        foreach (var ex in ae.InnerExceptions)
            Console.WriteLine($"AE: {ex.GetType().Name}: {ex.Message}");
    }
    totalErrors += roundErrors;
    Console.WriteLine($"Round {numThreads}x{itersPerThread}: Decoded={decoded}, Errors={roundErrors}");
}

if (totalErrors > 0) Console.WriteLine($"FAILURE: {totalErrors} errors");
else Console.WriteLine("SUCCESS: All concurrent decodes completed without error");
