using System;
using System.Collections.Concurrent;
using System.IO;
using System.Runtime.InteropServices;
using System.Threading;
using System.Threading.Tasks;
using SkiaSharp;

Console.WriteLine($"SkiaSharp Version: {typeof(SKBitmap).Assembly.GetName().Version}");
Console.WriteLine($"OS: {RuntimeInformation.OSDescription}");
Console.WriteLine($"Arch: {RuntimeInformation.ProcessArchitecture}");
Console.WriteLine($"Runtime: {RuntimeInformation.FrameworkDescription}");

byte[] CreateTestPng(int size, SKColor bg)
{
    using var bmp = new SKBitmap(size, size);
    using var canvas = new SKCanvas(bmp);
    canvas.Clear(bg);
    using var paint = new SKPaint { Color = SKColors.White, IsAntialias = true };
    canvas.DrawCircle(size / 2f, size / 2f, size / 3f, paint);
    using var image = SKImage.FromBitmap(bmp);
    using var data = image.Encode(SKEncodedImageFormat.Png, 100);
    return data.ToArray();
}

var pngSmall = CreateTestPng(100, SKColors.Blue);
var pngMedium = CreateTestPng(500, SKColors.Green);
var pngLarge = CreateTestPng(1000, SKColors.Red);
var images = new[] { pngSmall, pngMedium, pngLarge };

const int numThreads = 100;
const int numIterationsPerThread = 200;
var errors = new ConcurrentBag<string>();
var totalDecoded = 0;
var barrier = new Barrier(numThreads);

// Test 1: byte array decode
var tasks = new Task[numThreads];
for (int i = 0; i < numThreads; i++)
{
    int threadId = i;
    tasks[i] = Task.Run(() =>
    {
        var localBytes = (byte[])images[threadId % 3].Clone();
        barrier.SignalAndWait();
        for (int j = 0; j < numIterationsPerThread; j++)
        {
            using var bitmap = SKBitmap.Decode(localBytes);
            if (bitmap == null)
                errors.Add($"T{threadId}/i{j}: Decode returned null");
            else
                Interlocked.Increment(ref totalDecoded);
        }
    });
}
Task.WaitAll(tasks);

// Test 2: stream decode
var errors2 = new ConcurrentBag<string>();
var totalDecoded2 = 0;
var barrier2 = new Barrier(numThreads);
var tasks2 = new Task[numThreads];
for (int i = 0; i < numThreads; i++)
{
    int threadId = i;
    tasks2[i] = Task.Run(() =>
    {
        var localBytes = (byte[])images[threadId % 3].Clone();
        barrier2.SignalAndWait();
        for (int j = 0; j < numIterationsPerThread; j++)
        {
            using var ms = new MemoryStream(localBytes);
            using var skStream = new SKManagedStream(ms);
            using var bitmap = SKBitmap.Decode(skStream);
            if (bitmap == null)
                errors2.Add($"T{threadId}/i{j}: null");
            else
                Interlocked.Increment(ref totalDecoded2);
        }
    });
}
Task.WaitAll(tasks2);

// Test 3: SKCodec path
var errors3 = new ConcurrentBag<string>();
var totalDecoded3 = 0;
var barrier3 = new Barrier(numThreads);
var tasks3 = new Task[numThreads];
for (int i = 0; i < numThreads; i++)
{
    int threadId = i;
    tasks3[i] = Task.Run(() =>
    {
        var localBytes = (byte[])images[threadId % 3].Clone();
        barrier3.SignalAndWait();
        for (int j = 0; j < numIterationsPerThread; j++)
        {
            using var data = SKData.CreateCopy(localBytes);
            using var codec = SKCodec.Create(data);
            if (codec == null) { errors3.Add($"null codec"); continue; }
            var info = codec.Info;
            using var bitmap = new SKBitmap(info.Width, info.Height, info.ColorType, info.AlphaType);
            var result = codec.GetPixels(bitmap.Info, bitmap.GetPixels());
            if (result != SKCodecResult.Success)
                errors3.Add($"GetPixels: {result}");
            else
                Interlocked.Increment(ref totalDecoded3);
        }
    });
}
Task.WaitAll(tasks3);

var allErrors = errors.Count + errors2.Count + errors3.Count;
Console.WriteLine($"Total errors: {allErrors}");
if (allErrors > 0) Environment.Exit(1);
else { Console.WriteLine("No errors"); Environment.Exit(0); }
