using SkiaSharp;
using System;
using System.Runtime.InteropServices;

Console.WriteLine($"SkiaSharp version: {typeof(SKBitmap).Assembly.GetName().Version}");
Console.WriteLine($"Runtime: {RuntimeInformation.FrameworkDescription}");
Console.WriteLine($"OS: {RuntimeInformation.OSDescription}");
Console.WriteLine($"Arch: {RuntimeInformation.ProcessArchitecture}");
Console.WriteLine();

Console.WriteLine("=== SKColorType values and bytes per pixel ===");
bool has24Bit = false;
foreach (SKColorType ct in Enum.GetValues(typeof(SKColorType)))
{
    var info = new SKImageInfo(1, 1, ct, SKAlphaType.Opaque);
    int bpp = info.BytesPerPixel;
    Console.WriteLine($"  {ct,-20} = {bpp} bytes/pixel");
    if (bpp == 3) has24Bit = true;
}
Console.WriteLine($"Has 24-bit (3 bytes/pixel) color type: {has24Bit}");
Console.WriteLine();

Console.WriteLine("=== Closest equivalent: Rgb888x ===");
var rgb888xInfo = new SKImageInfo(1, 1, SKColorType.Rgb888x, SKAlphaType.Opaque);
Console.WriteLine($"  Rgb888x bytes per pixel: {rgb888xInfo.BytesPerPixel}");
Console.WriteLine();

Console.WriteLine("=== Workaround: 24bpp to 32bpp Rgb888x ===");
int width = 4, height = 2;
byte[] pixels24 = new byte[width * height * 3];
for (int i = 0; i < width * height; i++)
{
    pixels24[i * 3 + 0] = (byte)(i * 32);
    pixels24[i * 3 + 1] = (byte)(255 - i * 32);
    pixels24[i * 3 + 2] = (byte)(128);
}
var bmpInfo = new SKImageInfo(width, height, SKColorType.Rgb888x, SKAlphaType.Opaque);
using var bitmap = new SKBitmap(bmpInfo);
var destPtr = bitmap.GetPixels();
unsafe
{
    byte* dest = (byte*)destPtr;
    int srcIndex = 0;
    for (int y = 0; y < height; y++)
        for (int x = 0; x < width; x++)
        {
            dest[0] = pixels24[srcIndex]; dest[1] = pixels24[srcIndex + 1];
            dest[2] = pixels24[srcIndex + 2]; dest[3] = 0xFF;
            dest += 4; srcIndex += 3;
        }
}
var pixel0 = bitmap.GetPixel(0, 0);
Console.WriteLine($"  Pixel(0,0): R={pixel0.Red} G={pixel0.Green} B={pixel0.Blue} A={pixel0.Alpha}");
Console.WriteLine($"  Expected:   R=0 G=255 B=128 A=255");
Console.WriteLine();

Console.WriteLine("=== Direct 24bpp InstallPixels attempt ===");
try
{
    var info24 = new SKImageInfo(width, height, SKColorType.Rgb888x, SKAlphaType.Opaque);
    using var bmp24 = new SKBitmap();
    var handle = GCHandle.Alloc(pixels24, GCHandleType.Pinned);
    try
    {
        bool installed = bmp24.InstallPixels(info24, handle.AddrOfPinnedObject(), width * 3);
        Console.WriteLine($"  InstallPixels with 24bpp stride: {(installed ? "succeeded" : "failed")}");
    }
    finally { handle.Free(); }
}
catch (Exception ex) { Console.WriteLine($"  Exception: {ex.GetType().Name}: {ex.Message}"); }
Console.WriteLine();
Console.WriteLine("Conclusion: No 24-bit (3 bytes/pixel) SKColorType exists. Must expand to 32bpp.");
