using System;
using System.IO;
using System.Runtime.InteropServices;
using SkiaSharp;

Console.WriteLine($"SkiaSharp version: {typeof(SKBitmap).Assembly.GetName().Version}");
Console.WriteLine($"OS: {RuntimeInformation.OSDescription}");
Console.WriteLine($"Arch: {RuntimeInformation.ProcessArchitecture}");
Console.WriteLine();

var pngFiles = new[]
{
    "baboon.png",      // 16-bit RGB
    "color-wheel.png", // 8-bit RGBA
    "index8.png",      // 8-bit colormap (palette)
    "testimage.png",   // 8-bit RGBA large
};

int failures = 0;
foreach (var file in pngFiles)
{
    var path = Path.Combine(AppContext.BaseDirectory, file);
    if (!File.Exists(path))
        path = Path.Combine(Directory.GetCurrentDirectory(), file);
    if (!File.Exists(path))
    {
        Console.WriteLine($"[SKIP] {file} - not found");
        continue;
    }

    var bmp = SKBitmap.Decode(path);
    if (bmp == null)
    {
        Console.WriteLine($"[FAIL] SKBitmap.Decode(\"{file}\") returned null");
        failures++;
        using var codec = SKCodec.Create(path, out var codecResult);
        Console.WriteLine($"       SKCodec.Create result: {codecResult}, codec null: {codec == null}");
    }
    else
    {
        Console.WriteLine($"[PASS] {file}: {bmp.Width}x{bmp.Height}, ColorType={bmp.ColorType}, AlphaType={bmp.AlphaType}");
        bmp.Dispose();
    }
}

Console.WriteLine();
Console.WriteLine("--- Stream decode test ---");
foreach (var file in pngFiles)
{
    var path = Path.Combine(AppContext.BaseDirectory, file);
    if (!File.Exists(path))
        path = Path.Combine(Directory.GetCurrentDirectory(), file);
    if (!File.Exists(path)) continue;

    using var stream = File.OpenRead(path);
    using var skStream = new SKManagedStream(stream);
    using var codec = SKCodec.Create(skStream, out var result);
    if (codec == null)
    {
        Console.WriteLine($"[FAIL] SKCodec.Create(stream) for {file}: result={result}");
        failures++;
    }
    else
        Console.WriteLine($"[PASS] {file}: codec OK, format={codec.EncodedFormat}, result={result}");
}

Console.WriteLine();
Console.WriteLine("--- Byte array decode test ---");
foreach (var file in pngFiles)
{
    var path = Path.Combine(AppContext.BaseDirectory, file);
    if (!File.Exists(path))
        path = Path.Combine(Directory.GetCurrentDirectory(), file);
    if (!File.Exists(path)) continue;

    var bytes = File.ReadAllBytes(path);
    using var data = SKData.CreateCopy(bytes);
    using var bmp = SKBitmap.Decode(data);
    if (bmp == null)
    {
        Console.WriteLine($"[FAIL] SKBitmap.Decode(SKData) for {file} returned null");
        failures++;
    }
    else
        Console.WriteLine($"[PASS] {file}: {bmp.Width}x{bmp.Height}");
}

Console.WriteLine();
Console.WriteLine($"Total failures: {failures}");
return failures > 0 ? 1 : 0;
