using SkiaSharp;
using System;
using System.IO;

string tifPath = args.Length > 0 ? args[0] : "test.tif";
Console.WriteLine($"Testing TIF file: {tifPath}");
Console.WriteLine($"File exists: {File.Exists(tifPath)}");
Console.WriteLine($"File size: {new FileInfo(tifPath).Length} bytes");

// Test 1: SKImage.FromEncodedData(string)
Console.WriteLine("\n--- Test 1: SKImage.FromEncodedData(string) ---");
try
{
    var img1 = SKImage.FromEncodedData(tifPath);
    Console.WriteLine($"Result: {(img1 != null ? $"Success ({img1.Width}x{img1.Height})" : "NULL")}");
    img1?.Dispose();
}
catch (Exception ex)
{
    Console.WriteLine($"Exception: {ex.GetType().Name}: {ex.Message}");
}

// Test 2: SKImage.FromEncodedData(byte[])
Console.WriteLine("\n--- Test 2: SKImage.FromEncodedData(byte[]) ---");
try
{
    byte[] bytes = File.ReadAllBytes(tifPath);
    var img2 = SKImage.FromEncodedData(bytes);
    Console.WriteLine($"Result: {(img2 != null ? $"Success ({img2.Width}x{img2.Height})" : "NULL")}");
    img2?.Dispose();
}
catch (Exception ex)
{
    Console.WriteLine($"Exception: {ex.GetType().Name}: {ex.Message}");
}

// Test 3: SKImage.FromEncodedData(Stream)
Console.WriteLine("\n--- Test 3: SKImage.FromEncodedData(Stream) ---");
try
{
    using var stream = File.OpenRead(tifPath);
    var img3 = SKImage.FromEncodedData(stream);
    Console.WriteLine($"Result: {(img3 != null ? $"Success ({img3.Width}x{img3.Height})" : "NULL")}");
    img3?.Dispose();
}
catch (Exception ex)
{
    Console.WriteLine($"Exception: {ex.GetType().Name}: {ex.Message}");
}

// Test 4: SKBitmap.Decode(string)
Console.WriteLine("\n--- Test 4: SKBitmap.Decode(string) ---");
try
{
    var bmp1 = SKBitmap.Decode(tifPath);
    Console.WriteLine($"Result: {(bmp1 != null ? $"Success ({bmp1.Width}x{bmp1.Height})" : "NULL")}");
    bmp1?.Dispose();
}
catch (Exception ex)
{
    Console.WriteLine($"Exception: {ex.GetType().Name}: {ex.Message}");
}

// Test 5: SKBitmap.Decode(byte[])
Console.WriteLine("\n--- Test 5: SKBitmap.Decode(byte[]) ---");
try
{
    byte[] bytes2 = File.ReadAllBytes(tifPath);
    var bmp2 = SKBitmap.Decode(bytes2);
    Console.WriteLine($"Result: {(bmp2 != null ? $"Success ({bmp2.Width}x{bmp2.Height})" : "NULL")}");
    bmp2?.Dispose();
}
catch (Exception ex)
{
    Console.WriteLine($"Exception: {ex.GetType().Name}: {ex.Message}");
}

// Test 6: SKBitmap.Decode(Stream)
Console.WriteLine("\n--- Test 6: SKBitmap.Decode(Stream) ---");
try
{
    using var stream2 = File.OpenRead(tifPath);
    var bmp3 = SKBitmap.Decode(stream2);
    Console.WriteLine($"Result: {(bmp3 != null ? $"Success ({bmp3.Width}x{bmp3.Height})" : "NULL")}");
    bmp3?.Dispose();
}
catch (Exception ex)
{
    Console.WriteLine($"Exception: {ex.GetType().Name}: {ex.Message}");
}

// Test 7: SKCodec - check what format Skia sees
Console.WriteLine("\n--- Test 7: SKCodec.Create(string) ---");
try
{
    var codec = SKCodec.Create(tifPath);
    if (codec != null)
    {
        Console.WriteLine($"Codec: {codec.EncodedFormat}, Size: {codec.Info.Width}x{codec.Info.Height}");
        codec.Dispose();
    }
    else
    {
        Console.WriteLine("Result: NULL (codec creation failed - format not supported)");
    }
}
catch (Exception ex)
{
    Console.WriteLine($"Exception: {ex.GetType().Name}: {ex.Message}");
}
