using SkiaSharp;
using System;
using System.IO;
using System.Runtime.InteropServices;

Console.WriteLine($"SkiaSharp Version: {typeof(SKBitmap).Assembly.GetName().Version}");
Console.WriteLine($"OS: {RuntimeInformation.OSDescription}");
Console.WriteLine($"Arch: {RuntimeInformation.ProcessArchitecture}");
Console.WriteLine($".NET: {RuntimeInformation.FrameworkDescription}");
Console.WriteLine();

var tifPath = "C 7 142 18-0002.tif";
if (!File.Exists(tifPath))
{
    Console.WriteLine($"ERROR: TIF file not found at {tifPath}");
    return 1;
}

Console.WriteLine($"TIF file size: {new FileInfo(tifPath).Length} bytes");
Console.WriteLine();

// Test 1: SKImage.FromEncodedData(string)
Console.WriteLine("=== Test 1: SKImage.FromEncodedData(string) ===");
var image1 = SKImage.FromEncodedData(tifPath);
Console.WriteLine($"Result: {(image1 == null ? "null (FAIL)" : $"{image1.Width}x{image1.Height}")}");
image1?.Dispose();
Console.WriteLine();

// Test 2: SKImage.FromEncodedData(byte[])
Console.WriteLine("=== Test 2: SKImage.FromEncodedData(byte[]) ===");
var bytes = File.ReadAllBytes(tifPath);
var image2 = SKImage.FromEncodedData(bytes);
Console.WriteLine($"Result: {(image2 == null ? "null (FAIL)" : $"{image2.Width}x{image2.Height}")}");
image2?.Dispose();
Console.WriteLine();

// Test 3: SKImage.FromEncodedData(Stream)
Console.WriteLine("=== Test 3: SKImage.FromEncodedData(Stream) ===");
using (var stream = File.OpenRead(tifPath))
{
    var image3 = SKImage.FromEncodedData(stream);
    Console.WriteLine($"Result: {(image3 == null ? "null (FAIL)" : $"{image3.Width}x{image3.Height}")}");
    image3?.Dispose();
}
Console.WriteLine();

// Test 4: SKBitmap.Decode(string)
Console.WriteLine("=== Test 4: SKBitmap.Decode(string) ===");
try
{
    var bitmap1 = SKBitmap.Decode(tifPath);
    Console.WriteLine($"Result: {(bitmap1 == null ? "null (FAIL)" : $"{bitmap1.Width}x{bitmap1.Height}")}");
    bitmap1?.Dispose();
}
catch (Exception ex)
{
    Console.WriteLine($"Exception: {ex.GetType().Name}: {ex.Message}");
}
Console.WriteLine();

// Test 5: SKBitmap.Decode(byte[])
Console.WriteLine("=== Test 5: SKBitmap.Decode(byte[]) ===");
try
{
    var bitmap2 = SKBitmap.Decode(bytes);
    Console.WriteLine($"Result: {(bitmap2 == null ? "null (FAIL)" : $"{bitmap2.Width}x{bitmap2.Height}")}");
    bitmap2?.Dispose();
}
catch (Exception ex)
{
    Console.WriteLine($"Exception: {ex.GetType().Name}: {ex.Message}");
}
Console.WriteLine();

// Test 6: SKBitmap.Decode(Stream)
Console.WriteLine("=== Test 6: SKBitmap.Decode(Stream) ===");
try
{
    using var stream2 = File.OpenRead(tifPath);
    var bitmap3 = SKBitmap.Decode(stream2);
    Console.WriteLine($"Result: {(bitmap3 == null ? "null (FAIL)" : $"{bitmap3.Width}x{bitmap3.Height}")}");
    bitmap3?.Dispose();
}
catch (Exception ex)
{
    Console.WriteLine($"Exception: {ex.GetType().Name}: {ex.Message}");
}
Console.WriteLine();

// Test 7: SKCodec.Create to check format detection
Console.WriteLine("=== Test 7: SKCodec.Create ===");
try
{
    using var codecStream = File.OpenRead(tifPath);
    var codec = SKCodec.Create(codecStream);
    if (codec == null)
        Console.WriteLine("Result: null - format not recognized by Skia codec");
    else
    {
        Console.WriteLine($"Result: {codec.EncodedFormat}, {codec.Info.Width}x{codec.Info.Height}");
        codec.Dispose();
    }
}
catch (Exception ex)
{
    Console.WriteLine($"Exception: {ex.GetType().Name}: {ex.Message}");
}

// Test 8: Verify that known formats work (sanity check)
Console.WriteLine();
Console.WriteLine("=== Test 8: Sanity check - PNG decode works ===");
using var sanityBmp = new SKBitmap(10, 10);
using var sanityCanvas = new SKCanvas(sanityBmp);
sanityCanvas.Clear(SKColors.Red);
using var sanityImage = SKImage.FromBitmap(sanityBmp);
using var sanityData = sanityImage.Encode(SKEncodedImageFormat.Png, 100);
var pngBytes = sanityData.ToArray();
var decoded = SKImage.FromEncodedData(pngBytes);
Console.WriteLine($"PNG decode: {(decoded == null ? "FAIL" : $"{decoded.Width}x{decoded.Height} OK")}");
decoded?.Dispose();

return 0;
