using SkiaSharp;

var inputRect = new SKRect();
inputRect.Left = 25;
inputRect.Right = 15;
inputRect.Bottom = 2;
inputRect.Top = 10;

Console.WriteLine($"Input:  Left={inputRect.Left}, Top={inputRect.Top}, Right={inputRect.Right}, Bottom={inputRect.Bottom}");

var resultRect = SKMatrix.CreateIdentity().MapRect(inputRect);

Console.WriteLine($"Result: Left={resultRect.Left}, Top={resultRect.Top}, Right={resultRect.Right}, Bottom={resultRect.Bottom}");

bool leftMatch = resultRect.Left == inputRect.Left;
bool rightMatch = resultRect.Right == inputRect.Right;
bool topMatch = resultRect.Top == inputRect.Top;
bool bottomMatch = resultRect.Bottom == inputRect.Bottom;

Console.WriteLine($"Left match:   {leftMatch} (expected {inputRect.Left}, got {resultRect.Left})");
Console.WriteLine($"Right match:  {rightMatch} (expected {inputRect.Right}, got {resultRect.Right})");
Console.WriteLine($"Top match:    {topMatch} (expected {inputRect.Top}, got {resultRect.Top})");
Console.WriteLine($"Bottom match: {bottomMatch} (expected {inputRect.Bottom}, got {resultRect.Bottom})");

if (leftMatch && rightMatch && topMatch && bottomMatch)
{
    Console.WriteLine("PASS: MapRect preserved non-standard rect");
    return 0;
}
else
{
    Console.WriteLine("FAIL: MapRect normalized the rect (swapped values)");
    return 1;
}
