using SkiaSharp;
using System;
using System.IO;
using System.Collections.Generic;

Console.WriteLine($"SkiaSharp version: {typeof(SKBitmap).Assembly.GetName().Version}");
Console.WriteLine();

const int width = 2000;
const int height = 2000;
byte[] pngBytes;

using (var bmp = new SKBitmap(width, height, SKColorType.Rgba8888, SKAlphaType.Premul))
{
    var rng = new Random(42);
    for (int y = 0; y < height; y++)
        for (int x = 0; x < width; x++)
            bmp.SetPixel(x, y, new SKColor((byte)rng.Next(256), (byte)rng.Next(256), (byte)rng.Next(256)));

    using var image = SKImage.FromBitmap(bmp);
    using var data = image.Encode(SKEncodedImageFormat.Png, 100);
    pngBytes = data.ToArray();
}

Console.WriteLine($"PNG: {pngBytes.Length} bytes ({pngBytes.Length / 1024.0 / 1024.0:F2} MB)");
Console.WriteLine();

foreach (var chunkSize in new[] { 2048, 4096, 4 * 1024 * 1024 })
{
    var reads = new List<(int requested, int returned)>();
    using var inner = new MemoryStream(pngBytes);
    using var chunked = new InstrumentedChunkedStream(inner, chunkSize, reads);
    using var bitmap = SKBitmap.Decode(chunked);

    int shortReads = 0;
    foreach (var (req, ret) in reads)
        if (ret < req && ret > 0) shortReads++;

    int lastRow = bitmap == null ? -1 : FindLastRow(bitmap, width, height);
    bool ok = lastRow == height - 1;

    Console.WriteLine($"[chunk={chunkSize,10}] reads={reads.Count}, short_reads={shortReads}, lastRow={lastRow}/{height - 1} {(ok ? "OK" : "TRUNCATED")}");
}

static int FindLastRow(SKBitmap bmp, int w, int h)
{
    for (int y = h - 1; y >= 0; y--)
        if (bmp.GetPixel(w / 2, y).Alpha > 0) return y;
    return -1;
}

class InstrumentedChunkedStream : Stream
{
    private readonly Stream _inner;
    private readonly int _chunkSize;
    private readonly List<(int requested, int returned)> _reads;

    public InstrumentedChunkedStream(Stream inner, int chunkSize, List<(int, int)> reads)
    {
        _inner = inner;
        _chunkSize = chunkSize;
        _reads = reads;
    }

    public override bool CanRead => true;
    public override bool CanSeek => true;
    public override bool CanWrite => false;
    public override long Length => _inner.Length;
    public override long Position { get => _inner.Position; set => _inner.Position = value; }

    public override int Read(byte[] buffer, int offset, int count)
    {
        int toRead = Math.Min(count, _chunkSize);
        int ret = _inner.Read(buffer, offset, toRead);
        _reads.Add((count, ret));
        return ret;
    }

    public override long Seek(long offset, SeekOrigin origin) => _inner.Seek(offset, origin);
    public override void SetLength(long value) => _inner.SetLength(value);
    public override void Write(byte[] buffer, int offset, int count) => throw new NotSupportedException();
    public override void Flush() => _inner.Flush();
}
