using SkiaSharp;

var imageInfo = new SKImageInfo(
    width: 200,
    height: 200,
    colorType: SKColorType.Rgba8888,
    alphaType: SKAlphaType.Premul);

using var surface = SKSurface.Create(imageInfo);
var canvas = surface.Canvas;

canvas.Clear(SKColors.White);
using var paint = new SKPaint
{
    IsAntialias = true
};

var vertices = new[] { new SKPoint(110, 20), new SKPoint(160, 200), new SKPoint(10, 200) };
var colors = new[] { SKColors.Red, SKColors.Green, SKColors.Blue };

canvas.DrawVertices(SKVertexMode.Triangles, vertices, colors, paint);

using var image = surface.Snapshot();
using var pngData = image.Encode(SKEncodedImageFormat.Png, 100);

Console.WriteLine($"SkiaSharp version: {typeof(SKSurface).Assembly.GetName().Version}");
Console.WriteLine($"PNG data size: {pngData?.Size ?? 0} bytes");

if (pngData != null && pngData.Size > 0)
{
    var filePath = "output.png";
    using (var fileStream = File.Create(filePath))
    {
        pngData.SaveTo(fileStream);
    }

    using var peekBitmap = new SKBitmap(imageInfo);
    surface.ReadPixels(imageInfo, peekBitmap.GetPixels(), imageInfo.RowBytes, 0, 0);

    var centerPixel = peekBitmap.GetPixel(93, 140);
    Console.WriteLine($"Center pixel (93,140): {centerPixel}");

    var topPixel = peekBitmap.GetPixel(110, 30);
    Console.WriteLine($"Near top vertex (110,30): {topPixel}");

    Console.WriteLine($"Paint color: {paint.Color}");

    bool isBlack = centerPixel.Red < 10 && centerPixel.Green < 10 && centerPixel.Blue < 10 && centerPixel.Alpha > 200;
    Console.WriteLine(isBlack ? "RESULT: Triangle appears BLACK (bug)" : "RESULT: Triangle appears COLORED (expected)");
}
