using System;
using System.IO;
using SkiaSharp;
using SkiaSharp.HarfBuzz;

var fontPath = Path.Combine(AppContext.BaseDirectory, "content-font.ttf");
if (!File.Exists(fontPath))
    fontPath = "content-font.ttf";

Console.WriteLine($"Font file exists: {File.Exists(fontPath)}");
Console.WriteLine($"Platform: {Environment.OSVersion}");
Console.WriteLine();

// Test 1: FromStream path (the broken path on Android)
Console.WriteLine("=== Test 1: SKTypeface.FromStream ===");
using (var stream = File.OpenRead(fontPath))
using (var tf = SKTypeface.FromStream(stream))
    TestShaping(tf, "FromStream");

Console.WriteLine();

// Test 2: FromData path (the workaround that works on Android)
Console.WriteLine("=== Test 2: SKTypeface.FromData ===");
using (var data = SKData.Create(fontPath))
using (var tf = SKTypeface.FromData(data))
    TestShaping(tf, "FromData");

Console.WriteLine();

// Test 3: FromFile path
Console.WriteLine("=== Test 3: SKTypeface.FromFile ===");
using (var tf = SKTypeface.FromFile(fontPath))
    TestShaping(tf, "FromFile");

Console.WriteLine();

// Test 4: FromFamilyName path (system font)
Console.WriteLine("=== Test 4: SKTypeface.FromFamilyName ===");
using (var tf = SKTypeface.FromFamilyName("Arial"))
    TestShaping(tf, "FromFamilyName");

void TestShaping(SKTypeface tf, string method)
{
    if (tf == null) { Console.WriteLine($"  [{method}] Typeface is NULL"); return; }
    Console.WriteLine($"  Typeface: {tf.FamilyName}, UnitsPerEm: {tf.UnitsPerEm}");
    using var shaper = new SKShaper(tf);
    using var paint = new SKPaint { TextSize = 64, Typeface = tf };
    var result = shaper.Shape("Hello world!", 0, 0, paint);
    Console.WriteLine($"  Result length: {result.Codepoints.Length}");
    Console.Write("  Codepoints: ");
    Console.WriteLine(string.Join(", ", result.Codepoints));
    var allZero = Array.TrueForAll(result.Codepoints, c => c == 0);
    Console.WriteLine($"  All codepoints zero: {allZero}");
    Console.WriteLine($"  BUG PRESENT: {(allZero ? "YES" : "NO")}");
}
