using SkiaSharp;

int largeWidth = 14034;
int largeHeight = 9921;
int maxWidth = 1024;
int maxHeight = 768;

Console.WriteLine($"Creating {largeWidth}x{largeHeight} bitmap ({(long)largeWidth * largeHeight * 4 / 1024 / 1024} MB)...");

using var largeBitmap = new SKBitmap(largeWidth, largeHeight);
using (var canvas = new SKCanvas(largeBitmap))
{
    canvas.Clear(SKColors.Red);
    using var paint = new SKPaint { Color = SKColors.Blue };
    canvas.DrawRect(new SKRect(largeWidth / 4f, largeHeight / 4f, largeWidth * 3f / 4f, largeHeight * 3f / 4f), paint);
}

using var tempImage = SKImage.FromBitmap(largeBitmap);
using var jpegData = tempImage.Encode(SKEncodedImageFormat.Jpeg, 85);
Console.WriteLine($"JPEG data: {jpegData.Size} bytes");

using var image = SKImage.FromEncodedData(jpegData);
Console.WriteLine($"Decoded: {image?.Width}x{image?.Height}");

int newWidth = 1024, newHeight = 724;
var info = new SKImageInfo(newWidth, newHeight);
using var surface = SKSurface.Create(info);
surface!.Canvas.Clear(SKColors.White);
surface.Canvas.DrawImage(image, new SKRect(0, 0, newWidth, newHeight));
surface.Canvas.Flush();

using var newImage = surface.Snapshot();
using var newData = newImage.Encode(SKEncodedImageFormat.Jpeg, 85);
Console.WriteLine($"Output: {newData.Size} bytes");

using var outImg = SKImage.FromEncodedData(newData);
using var outBmp = SKBitmap.FromImage(outImg!);
var center = outBmp.GetPixel(newWidth / 2, newHeight / 2);
var topLeft = outBmp.GetPixel(1, 1);
Console.WriteLine($"Center: {center}, TopLeft: {topLeft}");
bool isBlank = center == SKColors.White && topLeft == SKColors.White;
Console.WriteLine(isBlank ? "BLANK - BUG REPRODUCED" : "Content present - resize works");

using var largeBmpFromImage = SKBitmap.FromImage(image!);
