using System;
using System.Diagnostics;
using SkiaSharp;

const int Iterations = 200;
const int Width = 800;
const int Height = 800;

Console.WriteLine($"SkiaSharp version: {typeof(SKCanvas).Assembly.GetName().Version}");
Console.WriteLine($"Runtime: {System.Runtime.InteropServices.RuntimeInformation.FrameworkDescription}");
Console.WriteLine($"Architecture: {System.Runtime.InteropServices.RuntimeInformation.ProcessArchitecture}");
Console.WriteLine($"OS: {System.Runtime.InteropServices.RuntimeInformation.OSDescription}");
Console.WriteLine();

using (var warmup = new SKBitmap(1, 1))
using (var warmupCanvas = new SKCanvas(warmup))
    warmupCanvas.Clear(SKColors.White);

var radialTimes = new double[Iterations];
for (int i = 0; i < Iterations; i++)
{
    using var bitmap = new SKBitmap(Width, Height);
    using var canvas = new SKCanvas(bitmap);
    canvas.Clear(SKColors.White);
    var sw = Stopwatch.StartNew();
    using (var paint = new SKPaint())
    {
        paint.Shader = SKShader.CreateRadialGradient(
            new SKPoint(Width / 2f, Height / 2f),
            Math.Max(Width, Height) / 2f,
            new SKColor[] { SKColors.Black, SKColors.White },
            null, SKShaderTileMode.Clamp);
        canvas.DrawRect(new SKRect(0, 0, Width, Height), paint);
    }
    sw.Stop();
    radialTimes[i] = sw.Elapsed.TotalMilliseconds;
}

var linearTimes = new double[Iterations];
for (int i = 0; i < Iterations; i++)
{
    using var bitmap = new SKBitmap(Width, Height);
    using var canvas = new SKCanvas(bitmap);
    canvas.Clear(SKColors.White);
    var sw = Stopwatch.StartNew();
    using (var paint = new SKPaint())
    {
        paint.Shader = SKShader.CreateLinearGradient(
            new SKPoint(0, 0), new SKPoint(Width, Height),
            new SKColor[] { SKColors.Black, SKColors.White },
            null, SKShaderTileMode.Clamp);
        canvas.DrawRect(new SKRect(0, 0, Width, Height), paint);
    }
    sw.Stop();
    linearTimes[i] = sw.Elapsed.TotalMilliseconds;
}

var sweepTimes = new double[Iterations];
for (int i = 0; i < Iterations; i++)
{
    using var bitmap = new SKBitmap(Width, Height);
    using var canvas = new SKCanvas(bitmap);
    canvas.Clear(SKColors.White);
    var sw = Stopwatch.StartNew();
    using (var paint = new SKPaint())
    {
        paint.Shader = SKShader.CreateSweepGradient(
            new SKPoint(Width / 2f, Height / 2f),
            new SKColor[] { SKColors.Black, SKColors.White });
        canvas.DrawRect(new SKRect(0, 0, Width, Height), paint);
    }
    sw.Stop();
    sweepTimes[i] = sw.Elapsed.TotalMilliseconds;
}

var solidTimes = new double[Iterations];
for (int i = 0; i < Iterations; i++)
{
    using var bitmap = new SKBitmap(Width, Height);
    using var canvas = new SKCanvas(bitmap);
    canvas.Clear(SKColors.White);
    var sw = Stopwatch.StartNew();
    using (var paint = new SKPaint())
    {
        paint.Color = SKColors.Blue;
        canvas.DrawRect(new SKRect(0, 0, Width, Height), paint);
    }
    sw.Stop();
    solidTimes[i] = sw.Elapsed.TotalMilliseconds;
}

static double Median(double[] arr) { var sorted = (double[])arr.Clone(); Array.Sort(sorted); int mid = sorted.Length / 2; return sorted.Length % 2 == 0 ? (sorted[mid - 1] + sorted[mid]) / 2.0 : sorted[mid]; }
static double Average(double[] arr) { double sum = 0; for (int i = 0; i < arr.Length; i++) sum += arr[i]; return sum / arr.Length; }

Console.WriteLine($"Results ({Iterations} iterations, {Width}x{Height} bitmap):");
Console.WriteLine($"  Solid color:     avg={Average(solidTimes):F3}ms  median={Median(solidTimes):F3}ms");
Console.WriteLine($"  Linear gradient: avg={Average(linearTimes):F3}ms  median={Median(linearTimes):F3}ms");
Console.WriteLine($"  Sweep gradient:  avg={Average(sweepTimes):F3}ms  median={Median(sweepTimes):F3}ms");
Console.WriteLine($"  Radial gradient: avg={Average(radialTimes):F3}ms  median={Median(radialTimes):F3}ms");
Console.WriteLine();
Console.WriteLine($"Radial/Linear ratio: {Median(radialTimes) / Median(linearTimes):F2}x");
Console.WriteLine($"Radial/Sweep ratio:  {Median(radialTimes) / Median(sweepTimes):F2}x");
Console.WriteLine($"Radial/Solid ratio:  {Median(radialTimes) / Median(solidTimes):F2}x");
