using SkiaSharp;

var imagePath = args.Length > 0 ? args[0] : "/tmp/gray8-test.png";
Console.WriteLine($"SkiaSharp version: {typeof(SKBitmap).Assembly.GetName().Version}");

var bytes = File.ReadAllBytes(imagePath);
using var stream1 = new MemoryStream(bytes);
using var codec = SKCodec.Create(stream1);
if (codec == null) { Console.WriteLine("ERROR: SKCodec.Create returned null"); return 1; }

Console.WriteLine($"Codec info: {codec.Info.Width}x{codec.Info.Height}, ColorType={codec.Info.ColorType}, BytesPerPixel={codec.Info.BytesPerPixel}");

// Reporter's approach: decode with Gray8
var imageInfo = new SKImageInfo(codec.Info.Width, codec.Info.Height, SKColorType.Gray8);
var bitmap = SKBitmap.Decode(codec, imageInfo);
Console.WriteLine($"Result (Gray8 decode): bitmap is {(bitmap == null ? "NULL <- BUG" : "not null")}");
if (bitmap != null) { Console.WriteLine($"  ColorType: {bitmap.ColorType}, BytesPerPixel: {bitmap.BytesPerPixel}"); bitmap.Dispose(); }

// Default decode (should work)
using var stream2 = new MemoryStream(bytes);
using var codec2 = SKCodec.Create(stream2);
var bitmapDefault = SKBitmap.Decode(codec2!);
Console.WriteLine($"Result (default decode): bitmap is {(bitmapDefault == null ? "NULL" : "not null")}");
if (bitmapDefault != null) { Console.WriteLine($"  ColorType: {bitmapDefault.ColorType}, BytesPerPixel: {bitmapDefault.BytesPerPixel}"); bitmapDefault.Dispose(); }

// Manual GetPixels with Gray8
using var stream4 = new MemoryStream(bytes);
using var codec4 = SKCodec.Create(stream4);
var bitmapManual = new SKBitmap(codec4!.Info.Width, codec4.Info.Height, SKColorType.Gray8, SKAlphaType.Opaque);
var result = codec4.GetPixels(bitmapManual.Info, bitmapManual.GetPixels());
Console.WriteLine($"Result (manual GetPixels with Gray8): {result}");
bitmapManual.Dispose();

return 0;
