var palette = new SKColor[256];
palette[0] = SKColors.Black;
palette[1] = SKColors.Red;
// ... fill palette

// Tile indices stored compactly (1 byte per pixel)
byte[] tileIndices = new byte[8 * 8]; // 8x8 tile

// Expand to RGBA when rendering
var info = new SKImageInfo(8, 8, SKColorType.Rgba8888, SKAlphaType.Premul);
