    public static RectangleF TransformRectangle(SKMatrix m, SKRect bound)
    {
        var start = new SKPoint(bound.Left, bound.Top);
        var end = new SKPoint(bound.Right, bound.Bottom);
        var pts = new[] { start, end };

        TransformPoints(m, pts);

        return new SKRect(start.X, start.Y, end.X, end.Y);
    }

    public static void TransformVectors(SKMatrix m, SKPoint[] points)
    {

        foreach (var point in points)
        {
            // see http://math.stackexchange.com/questions/29257/find-2d-point-given-2d-point-and-transformation-matrix
            // transformvectors needs to IGNORE the translation! (see: http://stackoverflow.com/questions/3265169/matrix-transformpoints-vs-transformvectors)
            point.X = (m.ScaleX * point.X) + (m.SkewX * point.X); 
            point.Y = (m.SkewY * point.Y) + (m.ScaleY * point.Y); 
        }
    }

    public static void TransformPoints(SKMatrix m, SKPoint[] points)
    {
        foreach (var point in points)
        { 
            // see http://math.stackexchange.com/questions/29257/find-2d-point-given-2d-point-and-transformation-matrix
            point.X = (m.ScaleX*point.X) + (m.SkewX*point.X) + m.TransX;
            point.Y = (m.SkewY*point.Y) + (m.ScaleY*point.Y) + m.TransY;
        }
    }
