            Stream stream = assembly.GetManifestResourceStream("TicTacToe.wallpaper.jpg");
            SKManagedStream skStream = new SKManagedStream(stream);
            SKBitmap skBitmap = SKBitmap.Decode(skStream);

            SKImageInfo info = new SKImageInfo(200, 200);
            var pieceBitmap = new SKBitmap(info);

            using (var canvas = new SKCanvas(pieceBitmap))
            {
                canvas.DrawBitmap(skBitmap, 0, 0);
            }

            var streamPiece = new MemoryStream();

            try
            {
                pieceBitmap.LockPixels();
                IntPtr p;
                IntPtr pixels = pieceBitmap.GetPixels(out p); 
                using (var img = SKImage.FromPixels(pieceBitmap.Info, pixels, pieceBitmap.Width * pieceBitmap.BytesPerPixel))
                {
                    var data = img.Encode(SKEncodedImageFormat.Png,300);
                    data.SaveTo(streamPiece);
                    imageSource = ImageSource.FromStream(() => streamPiece);
                }
            }
            finally
            {
                pieceBitmap.UnlockPixels();
            }
