Code example

                    var window = IntPtr.Zero;
                    window = SDL.SDL_CreateWindow(".NET Core SDL2-CS Tutorial",
                        SDL.SDL_WINDOWPOS_CENTERED,
                        SDL.SDL_WINDOWPOS_CENTERED,
                        1020,
                        800,
                        SDL.SDL_WindowFlags.SDL_WINDOW_RESIZABLE
                    );

                    var sdlContext = SDL.SDL_GL_CreateContext(window);
                    SDL.SDL_GL_MakeCurrent(window, sdlContext);
                     
                    GRGlInterface glInterface = GRGlInterface.AssembleGlInterface(sdlContext, (contextHandle, name) => {
                        var addr = SDL.SDL_GL_GetProcAddress(name);
                        return addr;
                    });

                    GRContext context = GRContext.Create(GRBackend.OpenGL, glInterface);
                    GRBackendRenderTargetDesc backendRenderTargetDescription = new GRBackendRenderTargetDesc
                    {
                        Config = GRPixelConfig.Rgba8888,
                        Height = 300,
                        Width = 300,
                        Origin = GRSurfaceOrigin.TopLeft,
                        RenderTargetHandle = new IntPtr(0),
                        SampleCount = 0,
                        StencilBits = 8
                    };

                    using (var surface = SKSurface.Create(context, backendRenderTargetDescription))
                    {
                        var canvas = surface.Canvas; 
                            canvas.Clear(SKColors.Red);
                    }
